import React, {useState} from "react";


const [data, setData] = useState("");
export const addBook = (e) =>{
    e.preventDefault()
    const book ={data}
    fetch ('api/books/add',{
        method:"POST",
        headers:{"Content-Type":"application/json"},
        body:JSON.stringify(book)
    }).then(()=>{
        console.log("Новая книга добавлена")
    })
}
