import React, {useEffect, useState} from 'react';

import {Grommet, Box, FormField, CheckBoxGroup} from 'grommet';
import { grommet} from 'grommet/themes';
import Form from "grommet/components/DateInput/stories/Form";
import Button from "@material-ui/core/Button";

export default function Genres ()  {
    const [data, setData] = useState([]);
    const [value, setValue] = useState();
    useEffect(() => {
        fetch('api/genres')
            .then((response) => response.json())
            .then((data) => setData(data));
    }, []);
    return (
        <Grommet theme={grommet}>
            <Box pad="medium" width="medium">
                <Form
                    onSubmit={({ value: values, touched }) =>
                        // 'touched' is a single boolean value indication of
                        // whether any of the checkboxes had changed.
                        console.log('Submit', values, touched)
                    }
                > {data.map(item =>
                    <FormField id="check-box-formfield-id" name="controlled" key={item.id}>
                        <CheckBoxGroup
                            id="check-box-group-id"
                            name="controlled"
                            aria-labelledby="check-box-formfield-id"
                            value={value}
                            onChange={({ value: nextValue }) => setValue(nextValue)}
                            options={item(item.title)}
                        />
                    </FormField>
                )}
                    <Button type="submit" label="Submit" />
                </Form>
            </Box>
        </Grommet>
    );
};
