import React, {Component} from "react";
// import Paper from "@material-ui/core/Paper";
// import {DataGrid} from '@material-ui/data-grid';
import {Box, Card, Heading, CardBody, CardHeader, Grid, Grommet, Text, Image, Stack} from 'grommet';
// import { FormDown, FormUp, Favorite } from 'grommet-icons';

const theme = {
    global: {
        font: {
            family: `-apple-system,
         BlinkMacSystemFont, 
         "Segoe UI", 
         Roboto`,
        },
    },
    heading: { font: { family: 'Comic Sans MS' } },
    card: {
        container: {
            elevation: 'large',
        },
        footer: {
            pad: 'medium',
        },
    },
};

export class MainContextComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            books: []
        }
    };

    async componentDidMount() {
        const response = await fetch('/api/books');
        const body = await response.json();
        this.setState({books: body});
    }

    render() {
        return (
            <Grommet theme={theme}>
                <Box pad="large">
                    <Grid
                        gap="large"
                        rows="small"
                        columns={{ count: 'fit', size: ['small', 'medium'] }}
                    >
                        {this.state.books.map(item => (
                            <Card width="medium" key={item.title}>
                                <Stack anchor="bottom-left">
                                    <CardBody height="medium">
                                        <Image
                                            fit="cover"
                                            src={item.link}
                                            a11yTitle="scuba diving"
                                        />
                                    </CardBody>
                                    <CardHeader
                                        pad={{ horizontal: 'small', vertical: 'small' }}
                                        // https://gist.github.com/lopspower/03fb1cc0ac9f32ef38f4#all-hex-value-from-100-to-0-alpha
                                        background="#000000A0"
                                        width="medium"
                                        justify="start"
                                    >
                                        <Box>
                                            <Heading level="3" margin="none">
                                                {item.title}
                                            </Heading>
                                            <Text size="medium">{item.year}</Text>
                                            <Text size="small">{item.review}</Text>
                                        </Box>
                                    </CardHeader>
                                </Stack>
                            </Card>
                        ))}
                    </Grid>
                </Box>
            </Grommet>
        )
    }
}
