import React, {Component} from "react";
// import Paper from "@material-ui/core/Paper";
// import {DataGrid} from '@material-ui/data-grid';
import {
    Box,
    Card,
    Heading,
    CardBody,
    Grid,
    Grommet,
    Text,
    Image,
    Stack,
    CardFooter,
    Paragraph,
    Collapsible,
    Anchor,
    Button
} from 'grommet';
import {FormDown, FormUp, Favorite} from 'grommet-icons';

const theme = {
    global: {
        font: {
            family: `Comic Sans MS, -apple-system,
         BlinkMacSystemFont, 
         "Segoe UI", 
         Roboto`,
        },
    },
    card: {
        elevation: 'none',
        background: 'light-2',
        footer: {
            pad: 'medium',
        },
    },
};

const ExpandButton = ({...rest}) => {
    const Icon = open ? FormUp : FormDown;
    return (
        <Button
            hoverIndicator="light-4"
            icon={<Icon color="brand"/>}
            {...rest}
        />
    );
};

export class MainContextComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            books: [],
            open:false,
            favorite:false,
        }
    };

    async componentDidMount() {
        const response = await fetch('/api/books');
        const body = await response.json();
        this.setState({books: body});
    }

    render() {
        return (
            <Grommet theme={theme}>
                <Box pad="large">
                    <Grid
                        gap="large"
                        rows="medium"
                        columns={{count: 'fit', size: ['small', 'medium']}}
                    >
                        {this.state.books.map(item => (
                            <Card elevation="large" width="medium">
                                <CardBody height="small">
                                    <Image
                                        fit="cover"
                                        src={item.link}
                                        a11yTitle={item.title}
                                    />
                                </CardBody>
                                <Box pad={{horizontal: 'medium'}} responsive={false}>
                                    <Heading level="3" margin={{vertical: 'medium'}}>
                                        Bridge
                                    </Heading>
                                    <Paragraph margin={{top: 'none'}}>
                                        {item.year}
                                    </Paragraph>
                                </Box>
                                <CardFooter>
                                    <Box direction="row" align="center" gap="small">
                                        <Button
                                            icon={<Favorite color={this.state.favorite ? 'red' : undefined}/>}
                                            hoverIndicator
                                            onClick={() => {
                                                this.setState({favorite:this.state.favorite(!this.state.favorite)});
                                            }}
                                        />
                                        <Anchor
                                            href="#"
                                            label="Узнать больше"
                                        />
                                    </Box>
                                    <ExpandButton onClick={() => setOpen(!this.state.open)}/>
                                </CardFooter>
                                <Collapsible open={this.state.open}>
                                    <Paragraph margin="medium" color="dark-3">
                                        {item.review}
                                    </Paragraph>
                                </Collapsible>
                            </Card>
                        ))}
                    </Grid>
                </Box>
            </Grommet>
        )
    }
}
