import React, {Component} from "react";
// import Paper from "@material-ui/core/Paper";
// import {DataGrid} from '@material-ui/data-grid';
import {
    Box,
    Card,
    Heading,
    CardBody,
    Grid,
    Grommet,
    Text,
    Image,
    Stack,
    CardFooter,
    Paragraph,
    Collapsible,
    Anchor,
    Button
} from 'grommet';
import {FormDown, FormUp, Favorite} from 'grommet-icons';

const theme = {
    global: {
        font: {
            family: `Comic Sans MS, -apple-system,
         BlinkMacSystemFont, 
         "Segoe UI", 
         Roboto`,
        },
    },
    card: {
        elevation: 'none',
        background: 'light-2',
        footer: {
            pad: 'medium',
        },
    },
};

export class MainContextComponent extends Component {

    state = {
        books: [],
        favorite: false,
    };

    async componentDidMount() {
        const response = await fetch('/api/books');
        const body = await response.json();
        this.setState({books: body});
    }

    onMarkFavorite =()=>{
        this.setState({
            favorite:true
        })
    }

    render() {
        const {favorite} = this.state;
        let classNames = 'heart';

        if(favorite){
            classNames +='red';
        }
        return (
            <Grommet theme={theme}>
                <Box pad="small">
                    <Grid
                        gap="small"
                        rows="medium"
                        columns={{count: 'fit', size: ['small', 'small']}}
                    >
                        {this.state.books.map(item => (
                            <Card elevation="medium" width="medium" key={item.id}>
                                <CardBody height="small">
                                    <Image
                                        fit="cover"
                                        src={item.link}
                                        a11yTitle={item.title}
                                    />
                                </CardBody>
                                <Box pad={{horizontal: 'medium'}} responsive={false}>
                                    <Heading level="4" margin={{vertical: 'medium'}}>
                                        {item.title}
                                    </Heading>
                                    <Paragraph margin={{top: 'none'}}>
                                        {item.year}
                                    </Paragraph>
                                </Box>
                                <CardFooter>
                                    <Box direction="row" align="center" gap="small">
                                        <Button
                                            icon={<Favorite className={classNames}/>}
                                            hoverIndicator
                                            onClick={this.onMarkFavorite}
                                        />
                                        <Anchor
                                            href="#"
                                            label="Узнать больше"
                                        />
                                    </Box>
                                </CardFooter>
                            </Card>
                        ))}
                    </Grid>
                </Box>
            </Grommet>
        )
    }
}
