import React, {useEffect, useState} from "react";
import {
    Box,
    Card,
    Heading,
    CardBody,
    Grid,
    Grommet,
    Image,
    CardFooter,
    Paragraph,
    Collapsible,
    Anchor,
    Button
} from 'grommet';
import {FormDown, FormUp, Favorite} from 'grommet-icons';

const theme = {
    global: {
        font: {
            family: `Comic Sans MS, -apple-system,
         BlinkMacSystemFont, 
         "Segoe UI", 
         Roboto`,
        },
    },
    card: {
        elevation: 'none',
        background: 'light-2',
        footer: {
            pad: 'medium',
        },
    },
};

export default function MainComponent() {

    const [data, setData] = useState([]);
    const [open, setOpen] = React.useState(false);
    const [favorite, setFavorite] = React.useState(false);
    useEffect(() => {
        fetch('api/books')
            .then((response) => response.json())
                .then((data) => setData(data));
    }, []);

        const ExpandButton = ({...rest}) => {
            const Icon = open ? FormUp : FormDown;
            return (
                <Button
                    hoverIndicator="light-4"
                    icon={<Icon color="brand"/>}
                    {...rest}
                />
            );
        };

        return (
            <Grommet theme={theme}>
                <Box pad="small">
                    <Grid
                        gap="small"
                        rows="medium"
                        columns={{count: 'fit', size: ['small', 'small']}}
                    >
                        {data.map(item => (
                            <Card elevation="medium" width="large" key={item.id}>
                                <CardBody height="small">
                                    <Image
                                        fit="cover"
                                        src={item.link}
                                        a11yTitle={item.title}
                                    />
                                </CardBody>
                                <Box pad={{horizontal: 'medium'}} responsive={false}>
                                    <Heading level="4" margin={{vertical: 'medium'}}>
                                        {item.title}
                                    </Heading>
                                    <Paragraph margin={{top: 'none'}}>
                                        {item.year}
                                    </Paragraph>
                                </Box>
                                <CardFooter>
                                    <Box direction="row" align="center" gap="small">
                                        <Button
                                                icon={<Favorite color={favorite ? 'red' : undefined} />}
                                                hoverIndicator
                                                onClick={() => {
                                                    setFavorite(!favorite);
                                                }}
                                        />
                                        <Anchor
                                            href="#"
                                            label="Узнать больше"
                                        />
                                    </Box>
                                    <ExpandButton onClick={() => setOpen(!open)} />
                                </CardFooter>
                                <Collapsible open={open}>
                                    <Paragraph margin="small" color="dark-3">
                                        {item.review}
                                    </Paragraph>
                                </Collapsible>
                            </Card>
                        ))}
                    </Grid>
                </Box>
            </Grommet>
        )
    }
